<?php


    function goodRequest(){
   $_SS2 = "";
        $pKeyP = base64_decode("");
        $pKey = openssl_pkey_get_public($pKeyP);
        if(!$pKey) return false;
        foreach($_GET As $key => $value) if($key!='_ss2') $_SS2 .= "{$value}|";
        $ok = openssl_verify($_SS2, base64_decode($_GET['_ss2']), $pKey);
        return ($ok === 1);
    }

?>